package test;
/*
 * 代码块
 *  局部代码块，构造代码块，静态代码快
 *  
 *  构造代码块：构造函数共有的块
 *  静态代码块：static修饰，只执行一次,先执行static ，再执行构造代码块
 */



class Code{
	
	int x;
	
	//构造代码块
	{
		int x = 1;
		System.out.println(x);
//		System.out.println();
	}
	
	public Code() {
		System.out.println("code");
	}
	public Code(int x,int y) {
		x = 2;
		y = 2;
		System.out.println(x);
	}
	
	static {
		int x = 3;
		System.out.println(x);
	}
}


