package test;

/*
 * 成员变量和局部变量
 * 成员变量有初始值，局部变量必须初始化
 * 
 */

class val{
	int mun ;
	
	public void study() {
		int mun = 1;
		System.out.println(mun);
	}
}

public class val0701 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		val v = new val();
		System.out.println(v.mun);
		v.study();
	}

}
